    return(
    <div>The app is used by pressing the buttons</div>
    )
  }
  return(
    <div>Button press history: {props.allClicks.join(' ')}</div>
  )
}
const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
